import React, { useContext } from "react";
import { ShopContext } from '../Context/ShopContext'; // Ensure ShopContext is imported correctly
import './CSS/ShopCategory.css'; 
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";

const ShopCategory = (props) => {
  const { allProducts } = useContext(ShopContext); // Ensure allProducts is destructured correctly

  // Filter items by category
  const filteredProducts = allProducts.filter(item => item.category === props.category);

  return (
    <div className='shop-category'>
      <img src={props.banner} alt=""/>

      <div className="Shopcategory-indexsort">
        <p>
          <span>Showing 1-{filteredProducts.length}</span> out of {allProducts.length} products
        </p>

        <div className="shopcategory-sort">
           Sort by <img src={dropdown_icon} alt=""/>
        </div>
      </div>

      <div className="shopcategory-products">
        {filteredProducts.map((item, i) => (
          <Item key={i} id={item.id} name={item.name} image={item.image} new_price={item.new_price} old_price={item.old_price}/>
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
